import React from 'react';
import TryAgainLogo from "../assets/img/try-again.gif";

const GameOverLayout = ({onRestart, board}) => {
    if (board.hasWon()) {
        return <div className="tile2048"></div>
    } else if (board.hasWon()) {
        return <div className="gameOver"></div>
    }
    
    return <div className="gameOver">
        <img src={TryAgainLogo} alt="Try Again" style={{
            width: "100%",
            height: "100%",
            cursor: "pointer"
        }} />
    </div>
}

export default GameOverLayout
